import { name as typeName } from "../types/literal";
export function register(serializer) {
    function write(type, value) {
        return serializer.write(type.type, value);
    }
    function read(type, input) {
        return serializer.read(type.type, input);
    }
    function readTrusted(type, input) {
        return serializer.readTrusted(type.type, input);
    }
    serializer.register({
        typeName,
        write,
        read,
        readTrusted,
    });
}
